import React, { Component } from "react";
import reaction_correct from '../C_dashboard/icons/reaction_correct.png';
import reaction_incorrect from '../C_dashboard/icons/reaction_incorrect.png';
  
class Like extends Component {

    state = { liked: false };

    toggle = () => {
        let localLiked = this.state.liked;

        // Toggle the state variable liked
        localLiked = !localLiked;
        this.setState({ liked: localLiked });
    };

    render() {
        return (
            <div>
                <div onClick={() => this.toggle()}>
                    {
                        this.state.liked === false ? 
                        ( <img src={reaction_correct} class="reso_author_profile" alt="icon"/> ) 
                        : ( <img src={reaction_incorrect} class="reso_author_profile" alt="icon"/> )
                    }
                </div>        
            </div>
        );
    }

}
  
export default Like;
