import React, { Component } from "react";
import { reaction_correct } from '../C_dashboard/icons/reaction_correct';
import { reaction_incorrect } from '../C_dashboard/icons/reaction_incorrect';
  
class Like extends Component {
  state = { liked: false };
  toggle = () => {
    let localLiked = this.state.liked;
  
    // Toggle the state variable liked
    localLiked = !localLiked;
    this.setState({ liked: localLiked });
  };
  render() {
    return (
      <div className="container">
        <center>
          <p>Click on the Like Button</p>
          <div
            className="container"
            style={{ border: "1px solid black", width: "15%" }}
            onClick={() => this.toggle()}
          >
            {this.state.liked === false ? (
              <FontAwesomeIcon icon={reaction_correct} />
            ) : (
              <FontAwesomeIcon icon={reaction_incorrect} />
            )}
          </div>
        </center>
      </div>
    );
  }
}
  
export default Like;
